definition.script:
//currentBuild.displayName = cycle
slackurl = 'https://mobiledgex.slack.com/services/hooks/jenkins-ci/'
slackcred = 'cdda7d70-b701-4983-b287-e3f46dde02e9'
slackchannel = '#qa-automation'

pipeline{
    agent { label slavenode }
    parameters {
        string(name: 'SlaveNode', defaultValue: 'jenkinsSlave1')
        booleanParam(name: 'BillingOrganization', defaultValue: true)
        booleanParam(name: 'Organization', defaultValue: true)
        booleanParam(name: 'AppInst', defaultValue: true)
        booleanParam(name: 'App', defaultValue: true)
        booleanParam(name: 'ClusterInst', defaultValue: true)
        booleanParam(name: 'Flavor', defaultValue: true)
        booleanParam(name: 'Cloudlet', defaultValue: true)
        booleanParam(name: 'AutoProvPolicy', defaultValue: true)
        booleanParam(name: 'AutoScalePolicy', defaultValue: true)
        booleanParam(name: 'User', defaultValue: true)
        //booleanParam(name: 'OrgCloudletPool', defaultValue: true)
        booleanParam(name: 'RateLimitSettings', defaultValue: true)
        booleanParam(name: 'TrustPolicy', defaultValue: true)
        booleanParam(name: 'TrustPolicyException', defaultValue: true)
        booleanParam(name: 'CloudletPool', defaultValue: true)
        booleanParam(name: 'CloudletPoolInvitation', defaultValue: true)
        booleanParam(name: 'CRMOverride', defaultValue: false)
        string(name: 'CloudletToClean')
    }

    stages {
        stage('Checkout') {
            steps {
                dir('go/src/github.com/mobiledgex') {
                    sh 'rm -rf *'
                    checkout([$class: 'GitSCM',
                        branches: [[name: 'master']],
                        doGenerateSubmoduleConfigurations: false,
                        extensions: [
                            [$class: 'SparseCheckoutPaths',  sparseCheckoutPaths:[
                                [$class:'SparseCheckoutPath', path:'tools/*'],
                                [$class:'SparseCheckoutPath', path:'modules/*']]]
                        ],
                        submoduleCfg: [],
                        userRemoteConfigs: [[credentialsId: 'xxxxxxxx-xxxx-xxxx-xxxx-xxxxxxxxxxxx',
                        url: 'https://github.com/mobiledgex/edge-cloud-qa.git']]])
                }
            }
        }
        stage("Cleanup provisioning") {
            steps {
                slackSend baseUrl: slackurl, tokenCredentialId: slackcred, channel: slackchannel, color: "good", message: "Cleaning Up Provisioning"
                dir('go/src/github.com/mobiledgex/tools') {
                    script {
                        table_list = ''
                        crmoverride_flag = ''
                        cloudlet_flag = ''
                        if (String.valueOf(params.BillingOrganization) == 'true') {
                            table_list = table_list + 'billingorg,'
                        }
                        if (String.valueOf(params.Organization) == 'true') {
                            table_list = table_list + 'org,'
                        }
                          if (String.valueOf(params.AppInst) == 'true') {
                            table_list = table_list + 'appinst,'
                        }
                        if (String.valueOf(params.App) == 'true') {
                            table_list = table_list + 'app,'
                        }
                        if (String.valueOf(params.ClusterInst) == 'true') {
                            table_list = table_list + 'clusterinst,'
                        }
                        if (String.valueOf(params.Flavor) == 'true') {
                            table_list = table_list + 'flavor,'
                        }
                        if (String.valueOf(params.Cloudlet) == 'true') {
                            table_list = table_list + 'cloudlet,'
                        }
                        if (String.valueOf(params.AutoProvPolicy) == 'true') {
                            table_list = table_list + 'autoprovpolicy,'
                        }
                        if (String.valueOf(params.AutoScalePolicy) == 'true') {
                            table_list = table_list + 'autoscalepolicy,'
                        }
                        if (String.valueOf(params.User) == 'true') {
                            table_list = table_list + 'user,'
                        }
                        //if (String.valueOf(params.OrgCloudletPool) == 'true') {
                        //    table_list = table_list + 'orgcloudletpool,'
                        //}
                        if (String.valueOf(params.RateLimitSettings) == 'true') {
                            table_list = table_list + 'ratelimitsettings,'
                        }
                        if (String.valueOf(params.TrustPolicy) == 'true') {
                            table_list = table_list + 'trustpolicy,'
                        }
                        if (String.valueOf(params.TrustPolicyException) == 'true') {
                            table_list = table_list + 'trustpolicyexception,'
                        }
                        if (String.valueOf(params.CloudletPool) == 'true') {
                            table_list = table_list + 'cloudletpool,'
                        }
                        if (String.valueOf(params.CloudletPoolInvitation) == 'true') {
                            table_list = table_list + 'cloudletpoolinvitation,'
                        }
                        if (String.valueOf(params.CRMOverride) == 'true') {
                            crmoverride_flag = ' --crmoverride'
                        }
                        if (params.CloudletToClean != '') {
                            cloudlet_flag = ' --cloudlet ' + params.CloudletToClean 
                        }
                        table_list = table_list.substring(0, table_list.length() - 1)
                        echo 'cleaning up ' + table_list
                        sh 'export PYTHONPATH=$WORKSPACE/go/src/github.com/mobiledgex/modules;python3 cleanprov.py --tables ' + table_list + ' --automation ' + cloudlet_flag + crmoverride_flag
                    }
                }
            }
            post {
                failure { 
                    echo "run clean failed"
                    //slackSend baseUrl: slackurl, tokenCredentialId: slackcred, channel: slackchannel, color: "fail", message: "Finished SDK tests with failure"
                }
                success {
                    echo "run clean tests passed"
                    //slackSend baseUrl: slackurl, tokenCredentialId: slackcred, channel: slackchannel, color: "good", message: "Finished SDK tests with success"
                }
            }
        }
    }

}
