definition.script:
currentBuild.displayName = cycle
//slackurl = 'https://mobiledgex.slack.com/services/hooks/jenkins-ci/'
//slackcred = 'cdda7d70-b701-4983-b287-e3f46dde02e9'
//slackchannel = '#qa-automation'

automated_component = "Automated"
testcase_components = [[automated_component]]

pipeline{
    agent { label slavenode }
    parameters {
        string(name: 'SlaveNode', defaultValue: 'jenkinsWindowsSelenium')
        string(name: 'Cycle', defaultValue: '2021-11-05_CirrusR3.1')
        string(name: 'Project', defaultValue: 'ECQ')
        booleanParam(name: 'RunFailedOnly', defaultValue: false)
        choice(name: 'TestTarget', choices: ['WebUI'], description: '')
        string(name: 'TestTimeout', defaultValue: '5m')
    }

    stages {
        stage("execute tests") {
            steps {
                //slackSend baseUrl: slackurl, tokenCredentialId: slackcred, channel: slackchannel, color: "good", message: "Starting SDK tests"

                script {
                    builds = [:]
                    testcase_components.eachWithIndex { value, index->
                        echo "${value}"
                        builds["${value[-1]}"] = {
                            build job: 'runTestcases', parameters: [
                                string(name: 'SlaveNode', value: params.SlaveNode),
                                string(name: 'Components', value: value.join(',')), 
                                string(name: 'Project', value: params.Project), 
                                string(name: 'Cycle', value: params.Cycle), 
                                string(name: 'TestTarget', value: params.TestTarget),
                                string(name: 'TestTimeout', value: params.TestTimeout),
                                booleanParam(name: 'RunFailedOnly', value: params.RunFailedOnly)]
                        }
                    }
                    parallel builds
                }
            }
            post {
                failure { 
                    echo "run Console tests failed"
                    //slackSend baseUrl: slackurl, tokenCredentialId: slackcred, channel: slackchannel, color: "fail", message: "Finished SDK tests with failure"
                }
                success {
                    echo "run Console tests passed"
                    //slackSend baseUrl: slackurl, tokenCredentialId: slackcred, channel: slackchannel, color: "good", message: "Finished SDK tests with success"
                }
            }
        }
    }

}
