                            echo "run VCD tests failed"
                            slack_fail('Finished VCD tests with failures')
                        }
                        success { 
                            echo "run VCD tests passed"
                            slack_good('Finished VCD tests with pass')
                        }
                    }
                }
                stage('Openstack Tests') {
                    steps {
                        slack_good('Starting Openstack tests')
                        script {
                            build job: 'runCrmTestcases', parameters: [
                                string(name: 'Project', value: project), 
                                string(name: 'Cycle', value: cycle), 
                                string(name: 'Controller', value: controller), 
                                string(name: 'MasterController', value: mastercontroller),
                                string(name: 'DME', value: dme),
