                                string(name: 'DMERestCert', value: dmerestcert),
                                string(name: 'TestTarget', value: 'Openstack'),
                                string(name: 'VariableFile', value: variablefile), 
                                string(name: 'CRMPool', value: params.CRMPoolOpenstack),
                                booleanParam(name: 'RunFailedOnly', value: runfailedonly)]
                        }
                    }
                    post {
